import numpy as npimport matplotlib.pyplot as plt########## DONNÉES#########t = np.array([0, 0.10, 0.20, 0.30, 0.40, 0.50, 0.60, 0.70, 0.80, 0.90, 1.00])X = np.array([0, 0.87, 1.73, 2.60, 3.46, 4.33, 5.20, 6.06, 6.93, 7.79, 8.66])Y = np.array([0, 0.45, 0.80, 1.05, 1.20, 1.25, 1.20, 1.05, 0.80, 0.45, 0.00])##################################################### REPRÉSENTATION GRAPHIQUE DES POSITIONS SUCCESSIVES####################################################fig = plt.figure(figsize=(8,6))# Le solplt.plot([-1,10], [0, 0], 'k')plt.fill_between([-1,10], [-1, -1], [0, 0], facecolor='k', alpha=0.25)# Les positions successivesc = plt.plot(X, Y, "o")################### VECTEURS VITESSE################### À compléter# Titre, nom des axes et légendeplt.title("Saut en longueur")plt.xlabel("X (m)")plt.ylabel("Y (m)")# Mise en forme du graphiqueticks = c[0].get_data()[1]ax = fig.axes[0]ax.set_xlim(-1,10)ax.set_ylim(-1, 5)ax.set_aspect("equal")for i in range(len(X)):    ax.annotate(r"$M_{%i}$"%(i), (X[i], Y[i]), textcoords="offset points", xytext=(0,10))plt.show()